html:
{% for row in doc.refilling_schedule %}
<div style="width: 95mm; height: 35mm; border: 1px solid #000; margin-bottom: 2mm; padding: 2mm; text-align: center;">

    <!-- QR Code Image (from qr_attach link) -->
    {% if row.qr_attach %}
        <img src="{{ row.qr_attach }}" style="width: 20mm; height: 20mm; object-fit: contain;">
    {% endif %}

    <!-- QR Code Text -->
    <div style="font-size: 10pt; margin-top: 2mm;">
        {{ row.qr_code }}
    </div>

    <!-- Location -->
    <div style="font-size: 9pt;">
        {{ row.location }}
    </div>

</div>
{% endfor %}
