{% set mobile_sa = frappe.db.get_value("Address",filters={'name':doc.shipping_address_name},fieldname='phone') %}
{% endif %}

{% if doc.company_address %}
{% set ads_1_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='address_line1') %}
{% set ads_2_com= frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='address_line2') %}
{% set city = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='city') %}
{% set state = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='gst_state') %}
{% set pin = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='pincode') %}
{% set coun = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='country') %}
{% set gst = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='gstin') %}
{% set state_code = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='gst_state_number') %}
{% set phone_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='phone') %}
{% set email_id_com = frappe.db.get_value("Address",filters={'name':doc.company_address},fieldname='email_id') %}
{% endif %}

{% if doc.company %}
{% set com_contact1 = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='phone_no') %}
{% set com_contact2 = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='fax') %}
{% set com_email = frappe.db.get_value("Company",filters={'name':doc.company},fieldname='email') %}
{% endif %}

{% if doc.customer %}
{% set customer_mobile = frappe.db.get_value("Customer", filters={"name":doc.customer}, fieldname="mobile_no") %}
{% set customer_email = frappe.db.get_value("Customer", filters={"name":doc.customer}, fieldname="email_id") %}
{% endif %}

{% set max_items_per_page = 12 %}
{% set items_list = [[]] %}

{% for row in doc.items %}
    {% set _= items_list[-1].append(row) %}
    {% if (row.idx % max_items_per_page)==0 %}
        {% set _= items_list.append([]) %}
    {% endif %}
{% endfor %}
{% set current_loop = [] %}

{% for items in items_list %}
    {% set _=current_loop.append(1) %}
<html>
    <div class="page">
     <span ><b><center>Delivery Note</center></b></span>
    <div class="header1">
        <div class="left-h" style="border-right:1px solid black;padding-right:5px">
            <div style="height:170px">
                <div style = "float:left; width:30%;padding-top:5px">
                    <img src="/files/logo_hfpes.jpg" style="">
                </div>
                <div style="float:right; width:70%;">
                    <p style="line-height: 1.4;"><b>{{doc.company.upper()}}</b></p>
                    <p>{{ads_1_com or ""}}</p>
                    <p>{{ads_2_com or ""}}, {{city}}</p>
                    {% if state %} <p>{{state or ""}}{% if pin %} - {{pin}}, {% endif %} {{coun}}</p> {% endif %}
                    <p>GSTIN/UIN-{{gst or ""}}</p>
                    <p style="font-size:12px">State Name: {{state or ""}}, Code: {{state_code or ""}}</p>
                    <p style="font-size:10px">Contact : 0416 2228101 / 2223101,9994578101 / 9543102101</p>
                    <p style="font-size:12px">E-Mail: {{email_id_com or 'admin@harshinifire.com'}}</p>
                </div>
            </div>
            <div style="clear: both;"></div>
            <div style="border-top:1px solid black; height:px">
                <p style="font-size:10px">Consignee (Ship to)</p> 
                <p><b>{{doc.customer_name}}</b></p> 
                <p >{{ads_1_sa or ""}}</p>
                <p >{{ads_2_sa or ""}}</p>
                 {% if state_sa %} <p>{{state_sa or ""}}{% if pin_sa %} - {{pin_sa}}, {% endif %} {{coun_sa}}</p> {% endif %}
                <p>GSTIN/UIN &nbsp; &nbsp; &nbsp;: {{gst_sa or ""}}</p>
                <p>State Name &nbsp; &nbsp;: {{state_sa or ""}}, Code: {{state_code_sa or ""}}</p>
                <p> &nbsp;</p>
                {% if mobile_sa %} <p>Contact  &nbsp; &nbsp;  &nbsp;: {{mobile_sa}}</p> {% endif %}
                {% if email_sa %} <p>Email   &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: {{email_sa}}</p> {% endif %}
            </div>
            <div style="border-top:1px solid black; height:px">
                <p style="font-size:10px">Consignee (Bill to)</p> 
                <p><b>{{doc.customer_name}}</b></p> 
                <p >{{ads_1_ba or ""}}</p>
                <p >{{ads_2_ba or ""}}</p>
                 {% if state %} <p>{{state or ""}}{% if pin %} - {{pin}}, {% endif %} {{coun}}</p> {% endif %}
                <p>GSTIN/UIN &nbsp; &nbsp; &nbsp;: {{gst_ba or ""}}</p>
                <p>State Name &nbsp; &nbsp;: {{state_a or ""}}, Code: {{state_code_ba or ""}}</p>
                <p> &nbsp;</p>
                {% if mobile_ba %} <p>Contact  &nbsp; &nbsp;  &nbsp;: {{mobile_ba}}</p> {% endif %}
                {% if email_ba %} <p>Email   &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: {{email_ba}}</p> {% endif %}
                <p></p>
            </div>
        </div>
        <div class="right-h">
             <table style="width:100%;font-size:12px; height:inherit">
                <tr class="tableheight">
                    <td style="border-right:1px solid black;">Delivery Note No.</td>
                    <td style="">Dated</td>
                </tr>

                <tr class="tableheight" >
                    <td style="border-right:1px solid black;border-bottom:1px solid black"><b>{{doc.name}}</b></td>
                    <td style="border-bottom:1px solid black"><b>{{doc.get_formatted('posting_date')}}</b></td>
                </tr>
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;">Sales Order</td>
                    <td style="">Mode/Terms of Payment</td>
                </tr>
                 {% set delivery_note =[] %} {% for i in doc.items %} {% if i.sales_order not in delivery_note %} {% set order=delivery_note.append(i.sales_order or "") %} {% endif %} {% endfor %}
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;border-bottom:1px solid black"><b>{{ delivery_note | join('') }}</b></td>
                    <td style="border-bottom:1px solid black"><b>30 DAYS</b></td>
                </tr>
                 <tr class="tableheight" >
                    <td style="border-right:1px solid black;">References No. & Date</td>
                    <td style="">Other References</td>
                </tr>
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;border-bottom:1px solid black"><b></b></td>
                    <td style="border-bottom:1px solid black"><b></b></td>
                </tr>
                 <tr class="tableheight" >
                    <td style="border-right:1px solid black;">Buyer's Order No.</td>
                    <td style="">Dated</td>
                </tr>
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;border-bottom:1px solid black"><b></b></td>
                    <td style="border-bottom:1px solid black"><b></b></td>
                </tr>
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;">Dispatch DocNo.</td>
                    <td style="">Delivery Trip</td>
                </tr>
                <tr class="tableheight" >
                    <td style="border-right:1px solid black;border-bottom:1px solid black"><b></b></td>
                    <td style="border-bottom:1px solid black"><b></b></td>
                </tr>

                <tr class="tableheight" >
                    <td style="border-right:1px solid black">Despatch through</td>
                    <td style="">Destination</td>
                    <td></td>
                </tr>
                 <tr class="tableheight" >
                    <td style="border-right:1px solid black; border-bottom:1px solid black"><b></b></td>
                    <td style="border-bottom:1px solid black"><b></b></td>
                    <td></td>
                </tr>
                 <tr >
                    <td style="">Terms of Delivery</td>
                </tr>

            </table>
        </div>
    </div>
    <div class="items">
        <table style="width:100%;">
            <thead >

            <tr style='border-bottom:1px solid black;border-top:1px solid black;color:solid black; font-size:12px;'>
                <td style="width:5;">SI No</td>
                <td style="width:41;border-left:1px solid black;"><center>Description of Goods</center></td>
                <td style="width:10%;border-left:1px solid black;"><center>HSN/SAC</center></td>
                <td style="width:9%;border-left:1px solid black;"><center>Quantity</center></td>
                <td style="width:8%;border-left:1px solid black;"><center>Rate</center></td>
                <td style="width:7%;border-left:1px solid black;"><center>per</center></td>
                <td style="width:7%;border-left:1px solid black;"><center>Disc ₹</center></td>
                <td style="width:13%;border-left:1px solid black;"><center>Amount</center></td>
            </tr>
             </thead>

              {% for row in items %}
             <tr>
                <td style="width:5;">{{row.idx}}</td>
                <td style="width:41%;border-left:1px solid black;font-size:15px"><b>{{row.item_name}}</b></td>
                <td style="width:10%;border-left:1px solid black;font-size:15px"><center>{{row.gst_hsn_code or ""}}</center></td>
                <td style="width:9%;border-left:1px solid black;"><center><b>{{row.qty or 0}} {{row.uom or ""}}</b></center></td>
                <td style="width:8%;border-left:1px solid black;font-size:15px"><center>{{"%.2f" % row.price_list_rate or 0.00}}</center></td>
                <td style="width:7%;border-left:1px solid black;font-size:15px"><center><b>{% if row.stock_uom == "Square Foot" %} Sqft {% else %} {{row.stock_uom or 0}} {% endif%}</b></center></td>
                 {% set dis_amt=row.discount_amount*row.qty %}
                <td style="width:7%;border-left:1px solid black;font-size:15px"><center> {{dis_amt or 0.00}}</center></td>
                <td style="width:13%;border-left:1px solid black;text-align:right;font-size:15px"><b>{{"%.2f" % row.amount or 0.00}}</b></td> 
             </tr>
             {% endfor %}
            {% if current_loop|length != items_list|length %}
            	<tr>
            	    <td style='border-top:1px solid black;border-bottom:1px solid black;padding-top: 0px !important;padding-bottom: 0px !important;' colspan=8>
            	        <center>
            	           <b>**To Be Continued**</b>
            	        </center>
            	    </td>
            	</tr>
        	{% endif %}
    {% if current_loop|length == items_list|length %}
        {% if doc.discount_amount %}
            <tr style="">
                <td style="width:5;"></td>
                <td style="text-align:right;width:41;border-left:1px solid black;"><b>Discount</b></td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:9%;border-left:1px solid black;"></td>
                <td style="width:8%;border-left:1px solid black;"></td>
                <td style="width:7%;border-left:1px solid black;"></td>
                <td style="width:7%;border-left:1px solid black;"></td>
                <td style="width:13%;border-left:1px solid black;text-align:right"><b>{{ doc.discount_amount or ""}}</b></td> 
            </tr>
        {% endif %}
        {% for row in range(0,max_items_per_page-(items|length),1) %}
            <tr>
                <td style="width:5;"></td>
                <td style="text-align:right;width:41;border-left:1px solid black;"><b>&nbsp;</b></td>
                <td style="width:10%;border-left:1px solid black;">&nbsp;</td>
                <td style="width:9%;border-left:1px solid black;"></td>
                <td style="width:8%;border-left:1px solid black;"></td>
                <td style="width:7%;border-left:1px solid black;">&nbsp;</td>
                <td style="width:7%;border-left:1px solid black;">&nbsp;</td>
                <td style="width:13%;border-left:1px solid black;text-align:right"><b>&nbsp;</b></td>  
            </tr>
        {% endfor %}
            <tr style="border-top:1px solid black; border-bottom:1px solid black;">
                 <td style="width:5;"></td>
                <td style="text-align:right;width:41;border-left:1px solid black;font-size:15px">Total</td>
                <td style="width:10%;border-left:1px solid black;"></td>
                <td style="width:9%;border-left:1px solid black;font-size:15px"><center><b>{{doc.total_qty or ""}}</b></center></td>
                <td style="width:8%;border-left:1px solid black;"></td>
                <td style="width:7%;border-left:1px solid black;"></td>
                <td style="width:7%;border-left:1px solid black;"></td>
                <td style="width:13%;border-left:1px solid black;text-align:right;font-size:15px"><b>{{doc.get_formatted('grand_total')}}</b></td> 
             </tr>
     {% endif %}
        </table>
        <div class="words" style="border-bottom:1px solid black">
            <p style="padding-top:3px; padding-left:2px">Amount Chargeable (in words) : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;E & O.E </p>
            <p style="padding-left:5px; font-size:14px"><b>{{doc.in_words}}</b></p>
            <p style="padding-left:5px; font-size:12px">Company's PAN : <b>BLUPA0583J</b></p>
        </div>
        <div class="declration" style="width:100%;height:2cm;font-size:11px">
            <div class="dec-left" style="padding-left:5px; font-size:11px">
                <p>Recd. in Good Condition</p>
            </div>
            <div class="dec-right" style="border-top:0px solid black">
               <p style="text-align:right; padding-right:5px; font-size:12px"><b>for {{doc.company}}</b></p>
               <p style="padding-left:10px; font-size:11px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Authorised Signatory</p>
           </div>
        </div>
    </div>
    <div style="page-break-after: always;"></div>
</html>
</div>
 {% endfor %}
