css:
.print-format td, .print-format th {
	padding: 1px !important;
	font-size:12px;
}

.print-format p {
    margin: 2px 0px 2px !important;
}

.print-format{
     margin-top:0.8cm;
      margin-right:0.5cm;
      margin-left:0.5cm;
      font-size:12px;
}

@page {
  margin: 1cm;
}

.header1{
    border:1px solid black;
    width:100%;
    height:230px;
}

.tableheight{
    height:15px;
}


.items table tr td:first-child {
    border-left: 1px solid black;
}

.items table tr td:last-child {
    border-right: 1px solid black;
}





.print-header {
    width: 100%;
}

.print-header td {
    width: 33%;
    border: 1px solid black;
    border-bottom: 0;
    padding: 3px !important;
}

.print-header td:nth-last-child(-n+2) {
    width: 16.5%;
}

.items tbody .item-row td {
    /*border-bottom: 1px solid black;*/
}

.flex {
    display: flex;
    width: 100%;
}

.flex > div {
    width: 100%;
}

.flex > div:nth-child(-n+2) {
    padding-left: 10px;
}

.flex > div:last-child td {
    padding-left: 3px !important;
}
.data{
    border-bottom: 0 ;
}
