css:
.print-pdf {
    border: 1px solid black;
    padding-top:30px;
    padding: 20px;
    width: 100%;
    font-size: 14px;
    text-align: center;
    font-family: Arial, sans-serif;
}

.title {
    font-size: 20px;
    font-weight: bold;
    text-transform: uppercase;
}

.company {
    font-size: 20px;
    text-transform: uppercase;
    color:#1854f3;
}

.address {
    font-size: 14px;
    margin-bottom: 20px;
}

.employee-details {
    width: 100%;
    margin-bottom: 40px;
    margin-top: 40px;
}

.employee-details td {
    padding: 5px;
    text-align: left;
}

.salary-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

.salary-table th, .salary-table td {
    border: 1px solid black;
    padding: 8px;
    text-align: left;
}

.salary-table th {
    background-color: #d3d3d3;
}

.total-row {
    font-weight: bold;
    background-color: #f0f0f0;
}

.net-pay-row {
    font-weight: bold;
    background-color: #b6bfe7;
}

.net-pay {
    font-size: 16px;
    font-weight: bold;
    color: black;
    margin-bottom: 40px;
    margin-top: 40px;
}

.signatures {
    display: flex;
    justify-content: space-between;
    margin-top: 50px;
    margin-bottom: 40px;
}

.footer-text {
    font-size: 12px;
    margin-top: 40px;
}
