css:
.print-format td, .print-format th {
	padding: 1px !important;

}
.print-format{
     margin-top:0.5cm;
      margin-right:1cm;
      margin-left:1cm;
      font-size:14px;
}
 @page {
      margin: 1.5cm;
    }
.header1{
    border:1px solid black;
    width:100%;
    height:285px;
    border-bottom:0px solid black;

}
.left-h {
    float: left;
    width: 55%;
    padding-left: 1px;
    padding-top: 5px;
    border-right: 1px solid black;
    font-size: 12px;
    line-height: 12px;
    box-sizing: border-box;
}

.right-h {
    float: right;
    width: 45%;
    height: 100%;
    box-sizing: border-box;
}
.tableheight{
    height:15px;
}
.header2{
    display:flex;
    border:1px solid black;
    width:100%;
    height:90px;
    border-top:0px solid black;
    border-bottom:0px solid black;
}
.header3{
    display:flex;
    border-left:1px solid black; 
    border-right:1px solid black; 
    width:100%;
    border-top:0px solid black;
    height:67px;
}
.left-h1{
    height:inherit;
    width:54.2%;
    float:left;
    padding-left:10px;
    border-right:1px solid black;
}

.right-h1{
    height:inherit;
    width:50%;
    float:right;
}
.items{
    width:100%;
    border:1px solid Black;
    border-top:0px solid black;
}
.words{
    width:100%;
    border-bottom:1px solid black;
    font-size:11px;
}
.gstin{
    width:100%;
    height:inherit;
    font-size:11px
}
.tax-words{
    width:100%;
  border-bottom:1px solid black;
}
.dec-left{
    width:50%;
    float:left;
    height:inherit;
}
.dec-right{
	width: 50%;
	float: right;
	border-left: 1px solid black;
	height: inherit;
	bottom: 0;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
}
