css:
.print-format{
     margin-top:150px;

}
body {
            font-family: Arial, sans-serif;
            line-height: 1.5;
            margin: 20px;
        }
        .header {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
            padding-left:20px;
        }
        .header div {
            font-size: 17px;
        }
        h1 {
            text-align: center;
            text-decoration: underline;
            font-size: 20px;
            margin: 10px 0;
        }
        .content {
            font-size: 17px;
            padding-left:20px;
            padding-top:20px;
        }
        .content .bold {
            font-weight: bold;
        }
        table {
            width: 100%;
            border-collapse: collapse;
            margin: 20px 0;
            font-size: 18px;
        }
        table, th, td {
            border: 1px solid black;
        }
        th, td {
            text-align: center;
            padding: 5px;
        }
        .footer {
            margin-top: 70px;
            padding-left:20px;
        }
        .footer .bold {
            font-weight: bold;
        }
        .note {
            margin-top: 20px;
            font-size: 9px;
            text-align: right;
        }
